import React from "react";
import { IconButton } from "@material-ui/core";
import ArrowForwardIcon from "@material-ui/icons/ArrowForward";
import ArrowBackIcon from "@material-ui/icons/ArrowBack";

function Section1(props) {
  const [slideLeft, setSlideLeft] = React.useState(0);
  const [hideButtonLeft, setHideButtonLeft] = React.useState(true);
  const [hideButtonRight, setHideButtonRight] = React.useState(false);
  const [sliderWidth, setSliderWidth] = React.useState(0);
  React.useEffect(() => {
    setSliderWidth(
      document.getElementById("hscroll").scrollWidth -
        document.getElementById("hscroll").offsetWidth
    );
  }, []);
  const moveRight = () => {
    const el = document.getElementById(`hscroll`);
    setSlideLeft((el.scrollLeft += 200));
  };

  const moveLeft = () => {
    const el = document.getElementById(`hscroll`);
    setSlideLeft((el.scrollLeft -= 200));
  };

  const onHScroll = () => {
    const el = document.getElementById(`hscroll`).scrollLeft;
    if (el > 0) {
      setHideButtonLeft(false);
    } else {
      setHideButtonLeft(true);
    }
    if (el < sliderWidth) {
      setHideButtonRight(false);
    } else {
      setHideButtonRight(true);
    }
  };
  return (
    <div>
      <section style={{ display: "flex", justifyContent: "space-between" }}>
        <IconButton onClick={moveLeft} disabled={hideButtonLeft}>
          <ArrowBackIcon
            style={{
              paddingTop: ".2rem",
              cursor: "pointer",
            }}
          />
        </IconButton>

        <IconButton onClick={moveRight} disabled={hideButtonRight}>
          <ArrowForwardIcon
            style={{
              paddingTop: ".2rem",
              cursor: "pointer",
            }}
          />
        </IconButton>
      </section>
      <div class="flex-container" id={`hscroll`} onScroll={() => onHScroll()}>
        <div style={{ minWidth: "300px" }}>
          <img
            src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
            height="300px"
            width="300px"
          />
        </div>
        <div>
          <img
            src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
            height="300px"
            width="300px"
          />
        </div>
        <div>
          <img
            src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
            height="300px"
            width="300px"
          />
        </div>
        <div>
          <img
            src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
            height="300px"
            width="300px"
          />
        </div>
        <div>
          <img
            src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
            height="300px"
            width="300px"
          />
        </div>
        <div>
          <img
            src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
            height="300px"
            width="300px"
          />
        </div>
      </div>
    </div>
  );
}

export default Section1;
