import React, { useRef, useContext } from "react";
import { ScrollMenu, VisibilityContext } from "react-horizontal-scrolling-menu";

function Section1(props) {
  const ref = useRef(null);
  const scroll = (scrollOffset) => {
    ref.current.scrollLeft += scrollOffset;
  };
  const { isLastItemVisible, scrollNext } = useContext(VisibilityContext);
  const { isFirstItemVisible, scrollPrev } = useContext(VisibilityContext);
  return (
    <div style={{ margin: 10, marginTop: 40 }}>
      <ScrollMenu
        apiRef={ref}
        LeftArrow={
          <div onClick={() => scroll(-20)} style={{ fontSize: "100px" }}>
            {" < "}
          </div>
        }
        RightArrow={
          <div style={{ fontSize: "100px" }} onClick={() => scroll(20)}>
            {" > "}
          </div>
        }
      >
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
      </ScrollMenu>
    </div>
  );
}

export default Section1;
