import React, { useEffect, useContext } from "react";
import { ThemeProvider, Typography } from "@mui/material";
import { createTheme } from "@mui/material";
import { UserContext } from "./StateMan";

import Section1 from "./Section1";
import Navbar from "./Navbar";

const theme = createTheme({
  typography: {
    fontFamily: ["Chilanka", "cursive"].join(","),
    fontSize: 14,
  },
});

const Firstpage = (props) => {
  useEffect(() => {
    const { HomeS, NthS } = useContext(UserContext);
    const [Home, setHome] = HomeS;
    const [Nth, setNth] = NthS;
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <Navbar />
      {Home ? (
        <div>
          <Section1 />
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "space-evenly",
              alignItems: "center",
              // border: "2px solid red",
              margin: 15,
              marginTop: 40,
              flexWrap: "wrap",
            }}
          >
            <div
              style={{
                width: 500,
                margin: 25,
                display: "flex",
                flexWrap: "wrap",
              }}
            >
              <Typography variant="h5">
                India’s largest space where Dreams meet destination{" "}
              </Typography>
              <Typography variant="h5" style={{ fontWeight: "bold" }}>
                We help you make informed choices at every step of your career!
              </Typography>
              <ul>
                <li>
                  <Typography>Right stream selection after 10th 📚</Typography>
                </li>
                <li>
                  <Typography>
                    Admission to dream college after 12th 🎓
                  </Typography>
                </li>
                <li>
                  <Typography>
                    Ideal transitioning to professional world 💼
                  </Typography>
                </li>
                <li>
                  <Typography>
                    Refining your study abroad prospects ✈️
                  </Typography>
                </li>
              </ul>
            </div>
            <div style={{ display: "flex", flexWrap: "wrap" }}>
              <img
                src="https://idreamcareer.com/wp-content/uploads/2021/08/career-counselling-for-students.webp"
                height="400px"
                width="400px"
              />
            </div>
          </div>
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "space-evenly",
              alignItems: "center",
              flexWrap: "wrap",
              margin: 15,
              marginTop: 40,
            }}
          >
            <div style={{ display: "flex", flexWrap: "wrap" }}>
              <img
                src="https://idreamcareer.com/wp-content/uploads/2021/08/career-counselling.webp"
                height="400px"
                width="400px"
              />
            </div>
            <div
              style={{
                width: 500,
                margin: 25,
                display: "flex",
                flexWrap: "wrap",
              }}
            >
              <Typography variant="h5">
                Why go for career guidance at iDreamCareer? 🤔
              </Typography>
              <Typography>
                We know seeking clarity on streams, college selection, overseas
                education, and career planning is extremely hard! Career
                counselling for students from the right place at the right time
                is the need of hour.
              </Typography>
              <Typography>
                That’s where we come into the picture! To help you focus on your
                dream career ❤
              </Typography>
            </div>
          </div>
        </div>
      ) : null}
    </ThemeProvider>
  );
};

export default Firstpage;
