import React, { useContext, useState, useEffect } from "react";
import CloseIcon from "@mui/icons-material/Close";
import {
  Button,
  InputLabel,
  MenuItem,
  Modal,
  Select,
  TextField,
  Typography,
} from "@mui/material";
import { UserContext } from "./StateMan";
import firebase from "../Firebase";

function ModalPage(props) {
  const { modalS } = useContext(UserContext);
  const [modal, setModal] = modalS;
  const [screenSize, setScreenSize] = useState(false);

  const [name, setName] = useState("");
  const [Vname, setVName] = useState("");
  const [age, setAge] = useState("");
  const [Vage, setVAge] = useState("");
  const [email, setEmail] = useState("");
  const [Vemail, setVEmail] = useState("");
  const [number, setNumber] = useState("");
  const [Vnumber, setVNumber] = useState("");
  const [classx, setClassx] = useState("none");
  const [Vclassx, setVClassx] = useState("none");
  const [cmnt, setCmnt] = useState("");

  const db = firebase.firestore();

  useEffect(() => {
    if (window.innerWidth < 600) {
      setScreenSize(true);
    } else setScreenSize(false);
  }, []);

  window.addEventListener("resize", () => Resize());
  const Resize = () => {
    if (window.innerWidth < 600) setScreenSize(true);
    else setScreenSize(false);
  };

  const handleSubmit = () => {
    if (name === "") {
      setVName("Enter Full name");
    }
    if (age === "") {
      setVAge("Enter your age");
    }
    if (email === "") {
      setVEmail("Enter your email id");
    }
    if (number === "") {
      setVNumber("Enter your mobile number");
    }
    // if (classx === "none") {
    //   setVEmail("Select your class");
    // }
    else {
      db.collection("data").add({
        name,
        age,
        email,
        number,
        classx,
        cmnt,
      });
      setModal(false);
      setVName("");
      setVAge("");
      setVEmail("");
      setVNumber("");
    }
  };
  return (
    <div>
      <Modal
        open={modal}
        onClose={() => setModal(false)}
        style={{
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <div
          style={{
            padding: 10,
            paddingInline: screenSize ? 40 : 10,
            // border: "2px solid red",
            backgroundColor: "white",
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            // flexWrap: "wrap",
            width: "55%",
            borderRadius: 30,
          }}
        >
          <div
            style={{
              display: "flex",
              flexDirection: "row",
              justifyContent: "center",
              alignItems: "center",
              flexWrap: "wrap",
              // border: "2px solid red",
              padding: 10,
            }}
          >
            <Typography
              variant="h5"
              style={{
                fontWeight: "bolder",
                color: "#5b5c5c",
              }}
            >
              Book a Free Session
            </Typography>
            <Button
              onClick={() => setModal(false)}
              style={{
                color: "black",
                position: "absolute",
                left: "70%",
                // border: "2px solid red",
              }}
            >
              <CloseIcon />
            </Button>
          </div>
          <div
            style={{
              display: "flex",
              alignItems: "center",
              // border: "2px solid red",
              flexWrap: "wrap",
              justifyContent: "center",
            }}
          >
            <div
              style={{
                // border: "2px solid red",
                width: screenSize ? "100%" : "40%",
              }}
            >
              <InputLabel style={{ margin: 10, marginBottom: 0 }}>
                Full Name
              </InputLabel>
              <TextField
                placeholder="Full Name"
                variant="outlined"
                value={name}
                error={Vname}
                helperText={Vname}
                style={{ margin: 10, width: "90%" }}
                onChange={(t) => {
                  if (t.target.value === "") {
                    setVName("Enter full name");
                  } else {
                    setVName("");
                  }
                  setName(t.target.value);
                }}
              />
            </div>
            <div
              style={{
                // border: "2px solid red",
                width: screenSize ? "100%" : "40%",
              }}
            >
              <InputLabel style={{ margin: 10, marginBottom: 0 }}>
                Age
              </InputLabel>
              <TextField
                placeholder="Age"
                variant="outlined"
                helperText={Vage}
                error={Vage}
                value={age}
                style={{ margin: 10, width: "90%" }}
                onChange={(t) => setAge(t.target.value)}
              />
            </div>
          </div>
          <div
            style={{
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              // border: "2px solid red",
              flexWrap: "wrap",
            }}
          >
            <div
              style={{
                width: screenSize ? "100%" : "40%",
                // border: "2px solid red",
              }}
            >
              <InputLabel style={{ margin: 10, marginBottom: 0 }}>
                Email Id
              </InputLabel>
              <TextField
                placeholder="Enter Email Id"
                variant="outlined"
                value={email}
                helperText={Vemail}
                error={Vemail}
                style={{ margin: 10, width: "90%" }}
                onChange={(t) => setEmail(t.target.value)}
              />
            </div>
            <div
              style={{
                width: screenSize ? "100%" : "40%",
                // border: "2px solid red",
              }}
            >
              <InputLabel style={{ margin: 10, marginBottom: 0 }}>
                Phone Number
              </InputLabel>
              <TextField
                placeholder="Phone Number"
                variant="outlined"
                value={number}
                helperText={Vnumber}
                error={Vnumber}
                inputProps={{ inputMode: "numeric" }}
                style={{ margin: 10, width: "90%" }}
                onChange={(t) => setNumber(t.target.value)}
              />
            </div>
          </div>
          <div
            style={{
              display: "flex",
              alignItems: "center",
              // border: "2px solid red",
              flexWrap: "wrap",
              justifyContent: "center",
            }}
          >
            <div
              style={{
                // border: "2px solid red",
                width: screenSize ? "100%" : "81%",
              }}
            >
              <InputLabel style={{ margin: 10, marginBottom: 0 }}>
                Class
              </InputLabel>
              <Select
                value={classx}
                onChange={(v) => setClassx(v.target.value)}
                style={{
                  margin: 10,
                  width: screenSize ? "90%" : "95%",
                  color: classx === "none" ? "#c4c4c4" : "black",
                }}
              >
                <MenuItem value="none" disabled style={{ color: "red" }}>
                  Enter your class
                </MenuItem>
                <MenuItem value={6}>6th</MenuItem>
                <MenuItem value={7}>7th</MenuItem>
                <MenuItem value={8}>8th</MenuItem>
                <MenuItem value={9}>9th</MenuItem>
                <MenuItem value={10}>10th</MenuItem>
                <MenuItem value={11}>11th</MenuItem>
                <MenuItem value={12}>12th</MenuItem>
              </Select>
            </div>
          </div>
          <div
            style={{
              // border: "2px solid red",
              display: "flex",
              justifyContent: "center",
            }}
          >
            <div style={{ width: screenSize ? "100%" : "81%" }}>
              <InputLabel style={{ margin: 10, marginBottom: 0 }}>
                Comment
              </InputLabel>
              <TextField
                placeholder="Comment"
                variant="outlined"
                value={cmnt}
                multiline
                rows={4}
                style={{ margin: 10, width: screenSize ? "90%" : "95%" }}
                onChange={(t) => setCmnt(t.target.value)}
              />
            </div>
          </div>

          <div
            style={{
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
              marginTop: 15,
            }}
          >
            <Button
              variant="contained"
              style={{ width: "40%", backgroundColor: "#fccc14", padding: 10 }}
              onClick={() => {
                handleSubmit();
              }}
            >
              Submit
            </Button>
          </div>
        </div>
      </Modal>
    </div>
  );
}

export default ModalPage;
