import * as React from "react";
import { ScrollMenu, VisibilityContext } from "react-horizontal-scrolling-menu";

function section1(props) {
  return (
    <div>
      {/* <ScrollMenu LeftArrow={LeftArrow} RightArrow={RightArrow}> */}
      <div
        style={{ height: 300, border: "2px solid red", overflowX: "scroll" }}
      >
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="300px"
          width="300px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="300px"
          width="300px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="300px"
          width="300px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="300px"
          width="300px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="300px"
          width="300px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="300px"
          width="300px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="300px"
          width="300px"
        />
      </div>
      {/* </ScrollMenu> */}
    </div>
  );
}

function LeftArrow() {
  const { isFirstItemVisible, scrollPrev } =
    React.useContext(VisibilityContext);

  return (
    <button
      disabled={isFirstItemVisible}
      onClick={() => scrollPrev()}
      style={{
        fontSize: 20,
        fontWeight: "bold",
        padding: 20,
      }}
    >
      {"<"}
    </button>
  );
}

function RightArrow() {
  const { isLastItemVisible, scrollNext } = React.useContext(VisibilityContext);

  return (
    <button
      disabled={isLastItemVisible}
      onClick={() => scrollNext()}
      style={{
        fontSize: 20,
        fontWeight: "bold",
        padding: 20,
      }}
    >
      {">"}
    </button>
  );
}

export default section1;
