import React, { useState, useEffect } from "react";
import { Typography, Button, ThemeProvider } from "@mui/material";
import { createTheme } from "@mui/material";
import PhoneIcon from "@mui/icons-material/Phone";
import DensityMediumIcon from "@mui/icons-material/DensityMedium";

function Navbar(props) {
  const [click, setClick] = useState(false);
  const [dropdown, setDropdown] = useState(false);

  useEffect(() => {
    if (window.innerWidth < 600) {
      setDropdown(true);
      console.log(dropdown);
    }
    console.log(window.innerWidth);
  }, []);
  return (
    <div>
      <header className="header">
        <div
          style={{
            marginLeft: "5%",
            display: "flex",
            alignItems: "center",
            justifyContent: "center",
            // border: "2px solid red",
          }}
        >
          <img
            src="https://idreamcareer.com/wp-content/uploads/2021/08/small-logo-1.webp"
            height="50px"
            width="80px"
          />
        </div>
        <div
          style={{
            display: dropdown ? "flex" : "none",
            marginLeft: "60%",
            marginTop: 10,
          }}
        >
          <DensityMediumIcon />
        </div>
        <div
          style={{
            display: dropdown ? "none" : "flex",
            flexDirection: "row",
            marginLeft: "24%",
            flexWrap: "wrap",
          }}
        >
          <div className="h1">
            <Typography style={{ color: "blue" }}>Home</Typography>
          </div>
          <div className="h1">
            <Typography>9th-10th</Typography>
          </div>
          <div className="h1">
            <Typography>11th-12th</Typography>
          </div>
          <div className="h1">
            <Typography>Undergraduate</Typography>
          </div>
          <div className="h1">
            <Typography>Study Abroad</Typography>
          </div>
          <div className="h1">
            <Typography>Blog</Typography>
          </div>
          <div className="h1">
            <PhoneIcon />
          </div>
          <div style={{ margin: 15 }}>
            <Button
              variant="contained"
              size="large"
              style={{
                backgroundColor: "#4c9fec",
                color: "white",
                padding: 5,
              }}
            >
              Book Now
            </Button>
          </div>
        </div>
      </header>
    </div>
  );
}

export default Navbar;
