import React, { useContext } from "react";
import { Typography, Button } from "@mui/material";
import { UserContext } from "./StateMan";
import slider1 from "../assests/slider1.jpg";

function SectionA(props) {
  const { modalS, eighttenS, eleventwelveS, screenSizeS } =
    useContext(UserContext);
  const [modal, setModal] = modalS;
  const [eightten, setEightten] = eighttenS;
  const [eleventwelve, setEleventwelve] = eleventwelveS;
  const [screenSize] = screenSizeS;

  return (
    <div
      style={{
        display: "flex",
        flexDirection: "row",
        alignItems: "center",
        justifyContent: "center",
        marginTop: 50,
        flexWrap: "wrap",
      }}
    >
      <div style={{ width: "50%", marginTop: 40 }}>
        <div
          style={{
            display: "flex",
            alignItems: "center",
            // justifyContent: "center",
            marginLeft: 100,
            flexWrap: "wrap",
          }}
        >
          <div style={{ width: 100 }}>
            <hr />
          </div>
          <div style={{ marginLeft: 10 }}>
            <Typography> CAREER COUNSELLING </Typography>
          </div>
        </div>
        {screenSize ? (
          <div
            style={{
              width: 400,
              display: "flex",
              flexWrap: "wrap",
            }}
          >
            <Typography
              style={{
                color: " #B3864D",
                fontSize: "36px",
                fontFamily: ["Playfair Display SC", "serif"],
              }}
            >
              LET'S CHANGE HOW AND WHY YOU WORK
            </Typography>
          </div>
        ) : (
          <div
            style={{
              width: 600,
              marginLeft: 200,
              display: "flex",
              flexWrap: "wrap",
            }}
          >
            <Typography
              style={{
                color: " #B3864D",
                fontSize: "76px",
                fontFamily: ["Playfair Display SC", "serif"],
              }}
            >
              LET'S CHANGE HOW AND WHY YOU WORK
            </Typography>
          </div>
        )}
        <div
          style={{
            display: "flex",
            justifyContent: "center",
            alignItems: "center",
          }}
        >
          <Button
            variant="contained"
            size="large"
            style={{
              backgroundColor: "white",
              width: 250,
              paddingBlock: 15,
              margin: 10,
              color: "#fccc14",
              marginLeft: 0,
              border: "1px solid black",
              borderRadius: 30,
              fontWeight: "bold",
              "&:hover": {
                backgroundColor: "#58595B",
                color: "white",
              },
            }}
            onClick={() => {
              setEightten(true);
              setModal(true);
            }}
          >
            Book Now For 9th-10th
          </Button>
          <Button
            variant="contained"
            size="large"
            style={{
              backgroundColor: "white",
              width: 250,
              paddingBlock: 15,
              margin: 10,
              color: "#fccc14",
              marginLeft: 20,
              border: "1px solid black",
              borderRadius: 30,
              fontWeight: "bold",
            }}
            onClick={() => {
              setEleventwelve(true);
              setModal(true);
            }}
          >
            Book For 11th-12th
          </Button>
        </div>
      </div>
      <div
        style={{
          display: "flex",
          justifyContent: "center",
          flexWrap: "wrap",
        }}
      >
        <img src={slider1} height="500px" width="600px" alt="" />
      </div>
      <div style={{ display: "none" }}>{(modal, eightten, eleventwelve)}</div>
    </div>
  );
}

export default SectionA;
