import * as React from "react";
import { ScrollMenu, VisibilityContext } from "react-horizontal-scrolling-menu";
import Styled from "../assests/styles1";


const sideScroll = (
    element: HTMLDivElement,
    speed: number,
    distance: number,
    step: number
  ) => {
    let scrollAmount = 0;
    const slideTimer = setInterval(() => {
      element.scrollLeft += step;
      scrollAmount += Math.abs(step);
      if (scrollAmount >= distance) {
        clearInterval(slideTimer);
      }
    }, speed);
  };
  
function Section1(props) {
    const contentWrapper = React.useRef(null);
  return (
    <Styled.Container>
    <Styled.Title>Horizontal Scroll Buttons</Styled.Title>
    <Styled.ContentWrapper ref={contentWrapper}>
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
        <img
          src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
          height="350px"
          width="350px"
        />
      </Styled.ContentWrapper>
      <Styled.ButtonWrapper>
        <Styled.Button
          onClick={() => {
            sideScroll(contentWrapper.current, 25, 100, -10);
          }}
        >
          Left
        </Styled.Button>
        <Styled.Button
          onClick={() => {
            sideScroll(contentWrapper.current, 25, 100, 10);
          }}
        >
          Right
        </Styled.Button>
      </Styled.ButtonWrapper>
    </Styled.Container>
  );
}

export default Section1;
