import React, { useState } from "react";
import { Typography, ThemeProvider, Button } from "@mui/material";
import { createTheme } from "@mui/material";
import PhoneIcon from "@mui/icons-material/Phone";
import { ScrollMenu, VisibilityContext } from "react-horizontal-scrolling-menu";

const theme = createTheme({
  typography: {
    fontFamily: ["Chilanka", "cursive"].join(","),
  },
});

const Firstpage = (props) => {
  const [items, setItems] = React.useState(getItems);
  const [selected, setSelected] = React.useState([]);
  const [position, setPosition] = React.useState(0);
  const isItemSelected = (id) => !!selected.find((el) => el === id);

  const handleClick =
    (id) =>
    ({ getItemById, scrollToItem }) => {
      const itemSelected = isItemSelected(id);

      setSelected((currentSelected) =>
        itemSelected
          ? currentSelected.filter((el) => el !== id)
          : currentSelected.concat(id)
      );
    };

  return (
    <ThemeProvider theme={theme}>
      <header className="header">
        <div className="h2">
          <div className="h1">
            <Typography style={{ color: "blue", textDecoration: "underline" }}>
              Home
            </Typography>
          </div>
          <div className="h1">
            <Typography>9th-10th</Typography>
          </div>
          <div className="h1">
            <Typography>11th-12th</Typography>
          </div>
          <div className="h1">
            <Typography>Undergraduate</Typography>
          </div>
          <div className="h1">
            <Typography>Study Abroad</Typography>
          </div>
          <div className="h1">
            <Typography>Blog</Typography>
          </div>
          <div className="h1">
            <PhoneIcon />
          </div>
          <div style={{ margin: 15 }}>
            <Button variant="contained" size="large">
              Book Now
            </Button>
          </div>
        </div>
      </header>
      <section1>
        <div>
          {/* <ScrollMenu LeftArrow={LeftArrow} RightArrow={RightArrow}>
            <img
              src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
              height="350px"
              width="350px"
            />
            <img
              src="https://images.unsplash.com/photo-1553272725-086100aecf5e?ixlib=rb-1.2.1&ixid=MnwxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=465&q=80"
              height="350px"
              width="350px"
            />
            <img
              src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
              height="350px"
              width="350px"
            />
            <img
              src="https://images.unsplash.com/photo-1553272725-086100aecf5e?ixlib=rb-1.2.1&ixid=MnwxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=465&q=80"
              height="350px"
              width="350px"
            />
            <img
              src="https://images.unsplash.com/photo-1642543492366-a92039433543?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=387&q=80"
              height="350px"
              width="350px"
            />
            <img
              src="https://images.unsplash.com/photo-1553272725-086100aecf5e?ixlib=rb-1.2.1&ixid=MnwxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=465&q=80"
              height="350px"
              width="350px"
            />
          </ScrollMenu> */}
          <ScrollMenu LeftArrow={LeftArrow} RightArrow={RightArrow}>
            {items.map(({ id }) => (
              <Card
                itemId={id} // NOTE: itemId is required for track items
                title={id}
                key={id}
                onClick={handleClick(id)}
                selected={isItemSelected(id)}
              />
            ))}
          </ScrollMenu>
        </div>
      </section1>
    </ThemeProvider>
  );
};
function LeftArrow() {
  const { isFirstItemVisible, scrollPrev } =
    React.useContext(VisibilityContext);

  return (
    <Arrow disabled={isFirstItemVisible} onClick={() => scrollPrev()}>
      Left
    </Arrow>
  );
}

function RightArrow() {
  const { isLastItemVisible, scrollNext } = React.useContext(VisibilityContext);

  return (
    <Arrow disabled={isLastItemVisible} onClick={() => scrollNext()}>
      Right
    </Arrow>
  );
}
function Card({ onClick, selected, title, itemId }) {
  const visibility = React.useContext(VisibilityContext);

  return (
    <div
      onClick={() => onClick(visibility)}
      style={{
        width: "160px",
      }}
      tabIndex={0}
    >
      <div className="card">
        <div>{title}</div>
        <div>visible: {JSON.stringify(!!visibility.isItemVisible(itemId))}</div>
        <div>selected: {JSON.stringify(!!selected)}</div>
      </div>
      <div
        style={{
          height: "200px",
        }}
      />
    </div>
  );
}
export default Firstpage;
